import styled from "styled-components";

export const FullContainer = styled.section`
  max-width: 400px;
  width: 400px;
  margin: 0 auto;
  padding: 20px;
  

  @media (min-width: 750px) {
    max-width: 750px;
    width: 750px;
    padding: 20px
  }

  @media (min-width: 1280px) {
    max-width: 1280px;
    width: 1280px;
  }
`

export const Container = styled.div`
  width: 100%
  background-color: teal;
  box-shadow: 0px 0px 5px #ccc;
  border-radius: 5px;
  padding: 16px;
  margin-bottom: 16px;
  background: teal;
  @media (min-width: 750px) {
    min-width: 50%;
    width: 50%;
  }
`;

export const InputContent = styled.div`
  display: flex;
  flex-direction: column;
  text-align: left;
`;

export const Label_Titulo = styled.label`
  font-size: 24px;
  font-weight: bold;
  text-align: left;

  @media (min-width: 750px) {
    font-size: 28px;
  }
`;

export const Label = styled.label`
  font-size: 14px;
  text-align: left;

  @media (min-width: 750px) {
    font-size: 16px;
  }
`;

export const LabelRadio = styled.label`
  font-size: 14px;
  text-align: left;
  margin-right: 12px;

  @media (min-width: 750px) {
    font-size: 16px;
  }
`

export const Input = styled.input`
  outline: none;
  border-radius: 5px;
  padding: 5px 10px;
  font-size: 15px;
  border: 1px solid #ccc;
  margin-right: 4px;
`;

export const RadioGroup = styled.div`
  display: flex;
  align-items: center;

  input {
    accent-color: black;
    margin-top: 0;
  }
`;

export const Button = styled.button`
  padding: 5px 10px;
  border: none;
  border-radius: 5px;
  cursor: pointer;
  color: white;
  background-color: teal;
`;
