import React, { useState } from "react";
import * as S from "./styled";

const FormularioTabela = () => {
  const [novaInstalacao, setNovaInstalacao] = useState("");
  const [consumoMedio, setConsumoMedio] = useState("");

  const handleChangeNovaInstalacao = (event) => {
    setNovaInstalacao(event.target.value);
  };

  const handleChangeConsumoMedio = (event) => {
    setConsumoMedio(event.target.value);
  };

  return (
    <S.FullContainer>
      <S.Container>
        <S.LabelTitulo fontSize="xl" fontWeight="bold">
          Formulário
        </S.LabelTitulo>
        <S.ContainerLinha>
          <S.InputContent>
            <S.Label>Instalação nova:</S.Label>
            <S.RadioGroup value={novaInstalacao} onChange={handleChangeNovaInstalacao}>
              <S.InputField type="radio" value="sim" />
              <S.LabelRadio>Sim</S.LabelRadio>
              <S.InputField type="radio" value="nao" />
              <S.LabelRadio>Não</S.LabelRadio>
            </S.RadioGroup>
          </S.InputContent>
        </S.ContainerLinha>
        <S.ContainerLinha>
          <S.InputContent>
            <S.Label>Consumo médio nos últimos 12 meses:</S.Label>
            <S.InputField
              type="text"
              value={consumoMedio}
              onChange={handleChangeConsumoMedio}
              placeholder="Digite o consumo médio"
            />
          </S.InputContent>
        </S.ContainerLinha>
      </S.Container>

      <S.Espaco />

      <S.Container>
        <S.LabelTitulo fontSize="xl" fontWeight="bold">
          Tabela de 3 colunas e 3 linhas:
        </S.LabelTitulo>
        
          <S.Tabela>
            
                <S.TabelaCabecalho>Coluna 1</S.TabelaCabecalho>
                <S.TabelaCabecalho>Coluna 2</S.TabelaCabecalho>
                <S.TabelaCabecalho>Coluna 3</S.TabelaCabecalho>
           
                <S.TabelaCelula>Linha 1, Coluna 1</S.TabelaCelula>
                <S.TabelaCelula>Linha 1, Coluna 2</S.TabelaCelula>
                <S.TabelaCelula>Linha 1, Coluna 3</S.TabelaCelula>
             
                <S.TabelaCelula>Linha 2, Coluna 1</S.TabelaCelula>
                <S.TabelaCelula>Linha 2, Coluna 2</S.TabelaCelula>
                <S.TabelaCelula>Linha 2, Coluna 3</S.TabelaCelula>
             
                <S.TabelaCelula>Linha 3, Coluna 1</S.TabelaCelula>
                <S.TabelaCelula>Linha 3, Coluna 2</S.TabelaCelula>
                <S.TabelaCelula>Linha 3, Coluna 3</S.TabelaCelula>
             
          </S.Tabela>
        
      </S.Container>
    </S.FullContainer>
  );
};

export default FormularioTabela;
