import styled from "styled-components";

export const FullContainer = styled.section`
  max-width: 400px;
  width: 100%;
  margin: 0 auto;
  margin-top: 20px;
  padding: 20px;
  box-shadow: 0px 0px 10px #ccc;
  border-radius: 10px;
  text-align: center;
  display: flex;
  justify-content: center;
  align-items: center;
  flex-direction: column; /* Adiciona esta linha para alinhar os filhos verticalmente */

  @media (min-width: 750px) {
    max-width: 750px;
    width: 100%;
    padding: 20px;
  }

  @media (min-width: 1280px) {
    max-width: 1280px;
    width: 100%;
  }
`;

export const Container = styled.div`
  width: 100%;
  box-shadow: 0px 0px 5px #ccc;
  border-radius: 5px;
  padding: 16px;
  margin-bottom: 16px;
  display: flex;
  justify-content: center;
  align-items: center;

  @media (min-width: 750px) {
    min-width: 50%;
    width: 50%;
  }
`;

export const Espaco = styled.div`
  margin-bottom: 20px;
`;

export const Tabela = styled.table`
  width: 100%;
  border-collapse: collapse;
`;

export const TabelaCabecalho = styled.th`
  border: 1px solid #ccc;
  padding: 10px;
  background-color: #f2f2f2;
  font-weight: bold;
`;

export const TabelaCelula = styled.td`
  border: 1px solid #ccc;
  padding: 10px;
`;

export const TabelaLinha = styled.tr`
  &:nth-child(even) {
    background-color: #f2f2f2;
  }
`;
