import React, { useState } from "react";
import { Box, Radio, RadioGroup, Stack, Text } from "@chakra-ui/react";
import * as C from "./styled";

const Form = () => {
  const [state, setState] = useState(() => ({
    first: '',
    second: '',
  }));

  const { first, second } = state;
  const containerProps = {
    mt: 4
  }
  
  const handleChange = (value, key) => setState(prevState => ({ 
    ...prevState,
    [key]:value
  }))

  return (
    <C.FullContainer>
        
        {/* Adicione o formulário abaixo */}
        <C.Container>
          <C.Label_Titulo>Tabela de 3 colunas e 3 linhas:</C.Label_Titulo>
          <C.Table>
            <thead>
              <tr>
                <C.TableHeaderCell>Coluna 1</C.TableHeaderCell>
                <C.TableHeaderCell>Coluna 2</C.TableHeaderCell>
                <C.TableHeaderCell>Coluna 3</C.TableHeaderCell>
              </tr>
            </thead>
            <tbody>
              <C.TableRow>
                <C.TableCell>Linha 1, Coluna 1</C.TableCell>
                <C.TableCell>Linha 1, Coluna 2</C.TableCell>
                <C.TableCell>Linha 1, Coluna 3</C.TableCell>
              </C.TableRow>
              <C.TableRow>
                <C.TableCell>Linha 2, Coluna 1</C.TableCell>
                <C.TableCell>Linha 2, Coluna 2</C.TableCell>
                <C.TableCell>Linha 2, Coluna 3</C.TableCell>
              </C.TableRow>
              <C.TableRow>
                <C.TableCell>Linha 3, Coluna 1</C.TableCell>
                <C.TableCell>Linha 3, Coluna 2</C.TableCell>
                <C.TableCell>Linha 3, Coluna 3</C.TableCell>
              </C.TableRow>
            </tbody>
          </C.Table>
        </C.Container>
    </C.FullContainer>
  );
};

const GroupInput = ({ value, onChange, label, description, containerProps }) => {

  return (
    <Box {...containerProps}>
      <Text as="b" fontSize="2xl">{label}</Text>
      <RadioGroup value={value} onChange={onChange}>
        <Stack direction="row">
          <Text as="label">{description}</Text>
          <Radio value="SIM">Sim</Radio>
          <Radio value="NAO">Não</Radio>
        </Stack>
      </RadioGroup>
    </Box>
  )

}

export default Form;
