import styled from "styled-components";

export const Header = styled.header`
  background: teal;
  padding: 20px;
  box-shadow: 0px 0px 5px #ccc;
`;

export const Title = styled.h1`
  color: #fff;
  font-size: 24px;
  font-weight: bold;

  @media (min-width: 1280px) {
    font-size: 32px;
  }
`;

export const SubTitle = styled.h2`
  color: #fff;
  font-size: 16px;

  @media (min-width: 1280px) {
    font-size: 24px;
  }
`;

export const Container_t = styled.div`
  max-width: 400px;
  margin: 10 auto;
  height: 80px;
  @media (min-width: 750px) {
    max-width: 750px;
  }

  @media (min-width: 800px) {
    max-width: 800px
  }
`;
