import styled from "styled-components";

export const FullContainer = styled.section`
  max-width: 400px;
  width: 400px;
  margin: 0 auto;
  padding: 20px;
  margin-top:20px;
  box-shadow: 0px 0px 5px #ccc;
  @media (min-width: 750px) {
    max-width: 750px;
    width: 750px;
    padding: 20px
  }

  @media (min-width: 1280px) {
    max-width: 1280px;
    width: 1280px;
  }
`

export const Container = styled.div`
  width: 100%
  background-color: #fff;
  box-shadow: 0px 0px 5px #ccc;
  border-radius: 5px;
  padding: 16px;
  margin-bottom: 16px;

  @media (min-width: 750px) {
    min-width: 50%;
    width: 50%;
  }
`;

export const InputContent = styled.div`
  display: flex;
  flex-direction: column;
  text-align: left;
`;

export const Label_Titulo = styled.label`
  font-size: 24px;
  font-weight: bold;
  text-align: left;

  @media (min-width: 750px) {
    font-size: 28px;
  }
`;
