import React, { useState } from "react";

import * as C from "./styled";


const FormularioTabela = () => {
  const [novaInstalacao, setNovaInstalacao] = useState("");
  const [consumoMedio, setConsumoMedio] = useState("");

  const handleChangeNovaInstalacao = (event) => {
    setNovaInstalacao(event.target.value);
  };

  const handleChangeConsumoMedio = (event) => {
    setConsumoMedio(event.target.value);
  };

  return (
    <FullContainer>
      {/* Formulário */}
      <Container>
        <LabelTitulo>
          Formulário
        </LabelTitulo>
        <div>
          <Label>Instalação nova: </Label>
          <RadioGroup>
            <input
              type="radio"
              value="sim"
              checked={novaInstalacao === "sim"}
              onChange={handleChangeNovaInstalacao}
            />
            <label>Sim</label>
            <input
              type="radio"
              value="nao"
              checked={novaInstalacao === "nao"}
              onChange={handleChangeNovaInstalacao}
            />
            <label>Não</label>
          </RadioGroup>
        </div>
        <div>
          <Label>Consumo médio nos últimos 12 meses:</Label>
          <InputField
            type="text"
            value={consumoMedio}
            onChange={handleChangeConsumoMedio}
            placeholder="Digite o consumo médio"
          />
        </div>
      </Container>

      {/* Espaço */}
      <Espaco />

      {/* Tabela */}
      <Container>
        <LabelTitulo>
          Tabela de 3 colunas e 3 linhas:
        </LabelTitulo>
        <Tabela>
          <thead>
            <tr>
              <TabelaCabecalho>Coluna 1</TabelaCabecalho>
              <TabelaCabecalho>Coluna 2</TabelaCabecalho>
              <TabelaCabecalho>Coluna 3</TabelaCabecalho>
            </tr>
          </thead>
          <tbody>
            <tr>
              <TabelaCelula>Linha 1, Coluna 1</TabelaCelula>
              <TabelaCelula>Linha 1, Coluna 2</TabelaCelula>
              <TabelaCelula>Linha 1, Coluna 3</TabelaCelula>
            </tr>
            <tr>
              <TabelaCelula>Linha 2, Coluna 1</TabelaCelula>
              <TabelaCelula>Linha 2, Coluna 2</TabelaCelula>
              <TabelaCelula>Linha 2, Coluna 3</TabelaCelula>
            </tr>
            <tr>
              <TabelaCelula>Linha 3, Coluna 1</TabelaCelula>
              <TabelaCelula>Linha 3, Coluna 2</TabelaCelula>
              <TabelaCelula>Linha 3, Coluna 3</TabelaCelula>
            </tr>
          </tbody>
        </Tabela>
      </Container>
    </FullContainer>
  );
};

export default FormularioTabela;
