import React, { useState } from "react";

import { Box, Radio, RadioGroup, Stack, Text } from "@chakra-ui/react";

import * as C from "../style";

const Form = () => {
 

  return (
    <C.FullContainer>
        <GroupInput 
          containerProps={containerProps} 
          value={first} 
          label="Alternativa 1" 
          description="Reconfiguração dos circuitos e remanejamento da carga:"
          onChange={(value) => handleChange(value, 'first')}
        />  
        <GroupInput 
          containerProps={containerProps} 
          value={second} label="Alternativa 2" 
          description="Definição de outro circuito eletrico para conexão da geração
              distribuída:"
          onChange={(value) => handleChange(value, 'second')} 
        /> 
    </C.FullContainer>
  );
};

const GroupInput = ({ value, onChange, label, description, containerProps }) => {

  return (
    <Box {...containerProps}>
      <Text as="b" fontSize="2xl">{label}</Text>
      <RadioGroup value={value} onChange={onChange}>
        <Stack direction="row">
          <Text as="label">{description}</Text>
          <Radio value="SIM">Sim</Radio>
          <Radio value="NAO">Não</Radio>
        </Stack>
      </RadioGroup>
    </Box>
  )

}

export default Form;
