import styled from 'styled-components';

export const FooterContainer = styled.footer`
background: teal;
  color: #fff;
  padding: 20px;
  text-align: center;
  margin-top: 20px;
`;

export const FooterText = styled.p`
  font-size: 20px;
  margin: 0;
`;
export const Title = styled.h2`
  color: #fff;
  font-size: 24px;
  font-weight: bold;

  @media (min-width: 1280px) {
    font-size: 32px;
  }
`;
