import React, { useState } from "react";
import { Box, Radio, RadioGroup, Stack, Text, Input, Table, Thead, Tbody, Tr, Th, Td } from "@chakra-ui/react";
import * as C from "./styled";

const FormularioTabela = () => {
  const [novaInstalacao, setNovaInstalacao] = useState("");
  const [consumoMedio, setConsumoMedio] = useState("");

  const handleChangeNovaInstalacao = (event) => {
    setNovaInstalacao(event.target.value);
  };

  const handleChangeConsumoMedio = (event) => {
    setConsumoMedio(event.target.value);
  };

  return (
    <C.FullContainer>
      {/* Formulário */}
      <C.Container>
        <Text fontSize="xl" fontWeight="bold">
          Formulário
        </Text>
        <Box>
          <Text>Instalação nova: </Text>
          <RadioGroup value={novaInstalacao} onChange={handleChangeNovaInstalacao}>
            <Stack direction="row">
              <Radio value="sim">Sim</Radio>
              <Radio value="nao">Não</Radio>
            </Stack>
          </RadioGroup>
        </Box>
        <Box>
          <Text>Consumo médio nos últimos 12 meses:</Text>
          <Input
            type="text"
            value={consumoMedio}
            onChange={handleChangeConsumoMedio}
            placeholder="Digite o consumo médio"
          />
        </Box>
      </C.Container>

      {/* Espaço */}
      

      {/* Tabela */}
      <C.Container>
        <Text fontSize="xl" fontWeight="bold">
          Tabela de 3 colunas e 3 linhas:
        </Text>
        <Table>
          <Thead>
            <Tr>
              <Th>Coluna 1</Th>
              <Th>Coluna 2</Th>
              <Th>Coluna 3</Th>
            </Tr>
          </Thead>
          <Tbody>
            <Tr>
              <Td>Linha 1, Coluna 1</Td>
              <Td>Linha 1, Coluna 2</Td>
              <Td>Linha 1, Coluna 3</Td>
            </Tr>
            <Tr>
              <Td>Linha 2, Coluna 1</Td>
              <Td>Linha 2, Coluna 2</Td>
              <Td>Linha 2, Coluna 3</Td>
            </Tr>
            <Tr>
              <Td>Linha 3, Coluna 1</Td>
              <Td>Linha 3, Coluna 2</Td>
              <Td>Linha 3, Coluna 3</Td>
            </Tr>
          </Tbody>
        </Table>
      </C.Container>
    </C.FullContainer>
  );
};

export default FormularioTabela;
